import React, {ReactElement} from 'react';
import {
    Button, IconButton, Paper, Table, TableBody,
    TableCell, TableContainer, TableRow, TextField
} from "@material-ui/core";
import {Order, Translation} from "../utils/types";
import CustomTableHead from "./CustomTableHead";
import {APP_ID, CONTEXT, DE, EN, FR, IT, STAGE, TEXT_ID} from "../utils/constants";
import DeleteIcon from '@material-ui/icons/Delete';


interface TranslationTableProps {
    translations: Array<Translation>,
    updateTranslation: (translation: Translation) => void,
    deleteTranslation: (translation: Translation) => void,
    handleSort: (property: keyof Translation) => void,
    setTranslations: (translations: Array<Translation>) => void,
    order: Order,
    orderBy: keyof Translation
}

function TranslationTable(props: TranslationTableProps): ReactElement {
    const {
        translations,
        setTranslations,
        updateTranslation,
        deleteTranslation,
        handleSort,
        order,
        orderBy,
    } = props;

    function handleChangeTranslationTable(e: React.ChangeEvent<HTMLInputElement>, index: number, key: keyof Translation): void {
        let currentTranslations: Array<Translation> = translations
        let translation: Translation = currentTranslations[index] // @ts-ignore
        translation[key] = e.target.value as string
        setTranslations([...currentTranslations])
    }

    function mapTranslationToTableRow(translation: Translation, index: number): ReactElement {
        return (

            <TableRow key={translation.id} className={"tableRow"}>
                <TableCell className={"appIdField"}>
                    <TextField name={APP_ID} value={translation.appId}
                               key={translation.id}
                               className={"appIdInput"}
                               label={"App-ID"} required
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, APP_ID)}
                    />
                </TableCell>
                <TableCell className={"contextField"}>
                    <TextField name={CONTEXT}
                               value={translation.context}
                               key={translation.id}
                               className={"contextInput"}
                               label={"Kontext"} required
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, CONTEXT)}
                    />
                </TableCell>
                <TableCell className={"textIdField"}>
                    <TextField name={TEXT_ID}
                               value={translation.textId}
                               key={translation.id}
                               className={"textIdInput"}
                               label={"Text-ID"} required
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, TEXT_ID)}
                    />
                </TableCell>
                <TableCell className={"stageField"}>
                    <TextField name={STAGE}
                               value={translation.stage}
                               key={translation.id}
                               className={"stageInput"}
                               label={"Stage"} required
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, STAGE)}
                    />
                </TableCell>
                <TableCell className={"deField"}>
                    <TextField name={DE}
                               value={translation.de}
                               key={translation.id}
                               className={"deInput"}
                               label={"Deutsch"}
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, DE)}
                    />
                </TableCell>

                <TableCell className={"frField"}>
                    <TextField name={FR}
                               value={translation.fr}
                               key={translation.id}
                               className={"frInput"}
                               label={"Französisch"}
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, FR)}
                    />
                </TableCell>
                <TableCell className={"itField"}>
                    <TextField name={IT}
                               value={translation.it}
                               key={translation.id}
                               className={"itInput"}
                               label={"Italienisch"}
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, IT)}
                    />
                </TableCell>
                <TableCell className={"enField"}>
                    <TextField name={EN}
                               value={translation.en}
                               key={translation.id}
                               className={"enInput"}
                               label={"Englisch"}
                               onChange={(e: React.ChangeEvent<HTMLInputElement>) => handleChangeTranslationTable(e, index, EN)}
                    />
                </TableCell>
                <TableCell className={"lastCallField"}>
                    <TextField
                        value={translation.lastCall ? translation.lastCall : '-'}
                        key={translation.id}
                        label={"Aufruf"}
                        className={"lastCallInput"} disabled
                    />
                </TableCell>
                <TableCell className={"buttonField"}>
                    <Button variant="contained"
                            color="primary"
                            id={"saveButton"}
                            onClick={() => updateTranslation(translation)}>
                        Speichern
                    </Button>
                </TableCell>
                <TableCell className={"buttonField"}>
                    <IconButton aria-label="delete"
                                id={"deleteButton"}
                                color={"secondary"}
                                onClick={() => deleteTranslation(translation)}>
                        <DeleteIcon fontSize="default"/>
                    </IconButton>
                </TableCell>
            </TableRow>
        );
    }


    return (
        <TableContainer component={Paper}>
            <Table>
                <CustomTableHead
                    handleSort={handleSort}
                    order={order}
                    orderBy={orderBy}
                />
                <TableBody>
                    {translations.map((translation, index) => mapTranslationToTableRow(translation, index))}
                </TableBody>
            </Table>
        </TableContainer>
        );
    }
    export default TranslationTable;
