import {ReactElement, useState} from 'react';
import {Button, Grid, TextField} from "@material-ui/core";
import {
    HTTP_POST,
    REACT_APP_BACKEND_URL,
    SEARCH_EMPTY_ERROR_MESSAGE, SEARCH_ERROR_MESSAGE,
    SEARCH_SUCCESS_MESSAGE
} from "../utils/apiUtils";
import {Order, Translation} from "../utils/types";
import {APP_ID, CONTEXT, STAGE, TEXT_ID} from "../utils/constants";

interface SearchProps {
    setTranslations: (translations: Array<Translation>) => void,
    sortTranslations: (translations: Array<Translation>, order: Order, orderBy: keyof Translation) => Array<Translation>,
    resetTable: () => void,
    showErrorAlert: (message: string) => void,
    showSuccessAlert: (message: string) => void
}

function Search(props: SearchProps): ReactElement {
    const {
        setTranslations,
        sortTranslations,
        resetTable,
        showErrorAlert,
        showSuccessAlert
    } = props
    const KEY_ENTER = 'Enter';
    const [appId, setAppId] = useState('');
    const [context, setContext] = useState('');
    const [textId, setTextId] = useState('');
    const [stage, setStage] = useState('');

    function handleChange(e: React.ChangeEvent<HTMLInputElement>): void {
        const eventName: string = e.target.name
        if (eventName === APP_ID) {
            setAppId(e.target.value.trim());
        } else if (eventName === CONTEXT) {
            setContext(e.target.value.trim());
        } else if (eventName === TEXT_ID) {
            setTextId(e.target.value.trim());
        } else if (eventName === STAGE) {
            setStage(e.target.value.trim());
        }
    }

    function handleKeyDown(e: React.KeyboardEvent): void {
        if (e.key === KEY_ENTER) {
            searchTranslation()
        }
    }

    function searchTranslation() {
        fetch(REACT_APP_BACKEND_URL + '/search', {
            method: HTTP_POST,
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({appId, textId, context, stage})
        })
            .then(response => response.json())
            .then(translations => {
                if (translations !== undefined && translations.length > 0) {
                    showSuccessAlert(SEARCH_SUCCESS_MESSAGE)
                    setTranslations(sortTranslations(translations, Order.asc, APP_ID))
                } else {
                    showErrorAlert(SEARCH_EMPTY_ERROR_MESSAGE)
                }
            })
            .catch(() => showErrorAlert(SEARCH_ERROR_MESSAGE));
    }

    function reset(): void {
        setAppId('')
        setContext('')
        setTextId('')
        setStage('')
        resetTable()
    }

    return (
        <Grid container direction={"row"} className={"searchContainer"}>
            <Grid item xs={2}>
                <TextField name={APP_ID}
                           label={'App-ID'}
                           onChange={handleChange}
                /> </Grid>
            value={appId}
            className={"searchInputField"}
            onKeyDown={handleKeyDown}
            <Grid item xs={2}>
                <TextField name={CONTEXT}
                           label={'Kontext'}
                           onChange={handleChange}
                           value={context}
                           className={"searchInputField"}
                           onKeyDown={handleKeyDown}
                /> </Grid>
            <Grid item xs={4}>
                <TextField name={TEXT_ID}
                           label={'Text-ID'}
                           onChange={handleChange}
                           value={textId}
                           className={"searchInputField"}
                           onKeyDown={handleKeyDown}
                /> </Grid>
            <Grid item xs={2}>
                <TextField name={STAGE}
                           label={'Stage'}
                           onChange={handleChange}
                           value={stage}
                           className={"searchInputField"}
                           onKeyDown={handleKeyDown}
                /> </Grid>
            <Grid item xs={1}>
                <Button variant="contained" color="primary" id={"searchButton"} onClick={searchTranslation}
                        name={'searchButton'}>
                    Suchen
                </Button>
            </Grid>
            <Grid item xs={1}>
                <Button variant="contained" color="inherit" id={"resetButton"} onClick={reset} name={'resetButton'}>
                    Reset
                </Button>
            </Grid>
        </Grid>
    );
}

export default Search;
