import React, {ReactElement, useEffect, useState} from 'react';
import {
    ADD_ERROR_MESSAGE,
    ADD_SUCCESS_MESSAGE,
    DELETE_ERROR_MESSAGE,
    DELETE_SUCCESS_MESSAGE,
    HTTP_DELETE,
    HTTP_GET,
    HTTP_POST,
    HTTP_PUT,
    HTTP_STATUS_CREATED,
    REACT_APP_BACKEND_URL,
    UPDATE_ERROR_MESSAGE,
    UPDATE_SUCCESS_MESSAGE
} from "../utils/apiUtils";
import TranslationTable from "./TranslationTable";
import {Order, Translation} from "../utils/types";
import {Button, Grid, MuiThemeProvider, Snackbar} from "@material-ui/core";
import {Alert, AlertTitle} from '@material-ui/lab';
import {createMuiTheme} from "@material-ui/core/styles";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from '@material-ui/icons/Close';
import Search from "./Search";
import {APP_ID} from "../utils/constants";

interface AlertInterface {
    isVisible: boolean,
    message: string,
    type?: AlertType
}

enum AlertType {
    success,
    error
}

const emptyAlert: AlertInterface = {
    isVisible: false,
    message: '',
    type: undefined
}
const emptyTranslation: Translation = {
    id: undefined,
    appId: '',
    context: '',
    textId: '',
    stage: '',
    de: '',
    fr: '',
    it: '',
    en: '',
    lastCall: undefined
}
const theme = createMuiTheme({
    palette: {
        primary: {
            light: '#3f51b5', main: '#2425aa', dark: '#002884', contrastText: '#fff',
        }, secondary: {
            light: '#ff7961',
            main: '#d32f2f',
            dark: '#ba000d',
            contrastText: '#fff',
        },
    },
});

function TranslationWrapper(): ReactElement {
    const [translations, setTranslations] = useState<Array<Translation>>([]);
    const [alert, setAlert] = useState(emptyAlert);
    const [order, setOrder] = React.useState<Order>(Order.asc);
    const [orderBy, setOrderBy] = React.useState<keyof Translation>(APP_ID);
    useEffect(() => {
        resetTable()
    }, [])
    const TEXT_ID_REGEX: RegExp = new RegExp(/^[a-z][\w-]*(?:\.[\w-]+)*$/)

    function fetchTranslations(): Promise<any> {
        return fetch(REACT_APP_BACKEND_URL + '/translation', {
            method: HTTP_GET,
        })
            .then(response => response.json())
            .catch((error) => showErrorAlert(error.toString()));
    }


    function updateTranslation(translation: Translation): void {
        if (isUserInputValid(translation)) {
            fetch(REACT_APP_BACKEND_URL + '/translation', {
                method: HTTP_POST,
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(translation)
            })
                .then(response => {
                    if (response.ok && response.status === 200) {
                        showSuccessAlert(UPDATE_SUCCESS_MESSAGE)
                    } else {
                        throw new Error();
                    }
                })
                .then(() => fetchTranslations()
                    .then((translations) => setTranslations(sortTranslations(translations, order, orderBy))))
                .catch(() => showErrorAlert(UPDATE_ERROR_MESSAGE));
        }
    }

    function deleteTranslation(translation: Translation): void {
        fetch(REACT_APP_BACKEND_URL + '/translation', {
            method: HTTP_DELETE,
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(translation)
        })
            .then(response => {
                if (response.ok && response.status === 200) {
                    showSuccessAlert(DELETE_SUCCESS_MESSAGE)
                } else {
                    throw new Error();
                }
            })
            .then(() => fetchTranslations()
                .then((translations) => setTranslations(sortTranslations(translations, order, orderBy))))
            .catch(() => showErrorAlert(DELETE_ERROR_MESSAGE));
    }


    function addTranslation(): void {
        fetch(REACT_APP_BACKEND_URL + '/translation', {
            method: HTTP_PUT,
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(emptyTranslation)
        })
            .then(response => {
                if (response.status === HTTP_STATUS_CREATED) {
                    showSuccessAlert(ADD_SUCCESS_MESSAGE)
                } else {
                    throw new Error();
                }
            })
            .then(() => fetchTranslations()
                .then((translations) => setTranslations(sortTranslations(translations, Order.asc, APP_ID))))
            .catch(() => showErrorAlert(ADD_ERROR_MESSAGE));
    }


    function isUserInputValid(translation: Translation): boolean {
        return validateTextId(translation.textId) && validateAppId(translation.appId) && validateContext(translation.context) &&
            validateStage(translation.stage);
    }

    function validateTextId(textId: string): boolean {
        textId = textId.trim()
        if (textId === '') {
            showErrorAlert('Das Feld Text-ID darf nicht leer sein')
            return false;
        } else if (!textId.match(TEXT_ID_REGEX)) {
            showErrorAlert('Das Feld Text-ID darf nur duch Punkte getrennt werden')
            return false;
        }
        return true;
    }

    function validateAppId(appId: string): boolean {
        if (appId.trim() === '') {
            showErrorAlert('Das Feld App-ID darf nicht leer sein')
            return false;
        }
        return true;
    }

    function validateContext(context: string): boolean {
        if (context.trim() === '') {
            showErrorAlert('Das Feld Kontext darf nicht leer sein')
            return false;
        }
        return true;
    }

    function validateStage(stage: string): boolean {
        if (stage.trim() === '') {
            showErrorAlert('Das Feld Stage darf nicht leer sein')
            return false;
        }
        return true;
    }

    function showErrorAlert(message: string): void {
        const alert: AlertInterface = {
            isVisible: true,
            message: message,
            type: AlertType.error
        }
        setAlert(alert)
    }


    function showSuccessAlert(message: string): void {
        const alert: AlertInterface = {
            isVisible: true,
            message: message,
            type: AlertType.success
        }
        setAlert(alert)
    }

    function getComparator(a: Translation, b: Translation, order: Order, orderBy: keyof Translation): number {
        return order === Order.asc ? compare(a, b, order, orderBy) : compare(b, a, order, orderBy)
    }

    function compare(a: Translation, b: Translation, order: Order, orderBy: keyof Translation): number {
        let valueA: string = a[orderBy] as string;
        let valueB: string = b[orderBy] as string;
        if (valueA === null) {
            return -1
        } else if (valueB === null) {
            return 1
        }
        return valueA.localeCompare(valueB);
    }

    function handleSort(property: keyof Translation): void {
        const isAsc = orderBy === property && order === Order.asc;
        setTranslations(sortTranslations(translations, isAsc ? Order.desc : Order.asc, property));
    }

    function sortTranslations(translations: Array<Translation>, order: Order, orderBy: keyof Translation): Array<Translation> {
        setOrderBy(orderBy)
        setOrder(order)
        return translations.sort((a, b) => getComparator(a, b, order, orderBy))
    }

    function resetTable(): void {
        fetchTranslations().then((translations) => setTranslations(sortTranslations(translations, Order.asc, APP_ID)));
    }


    return (
        <div className={"translationWrapper"}>
            <MuiThemeProvider theme={theme}>
                <Snackbar
                    anchorOrigin={{vertical: "top", horizontal: "center"}}
                    open={alert.isVisible && alert.type === AlertType.error}
                    autoHideDuration={null}
                >
                    <Alert severity="error" variant={"filled"}>
                        <IconButton onClick={() => setAlert(emptyAlert)} className={"alertCloseIcon"}>
                            <CloseIcon/>
                        </IconButton>
                        <AlertTitle>Error</AlertTitle>
                        {alert.message}
                    </Alert>
                </Snackbar>
                <Snackbar
                    anchorOrigin={{vertical: "top", horizontal: "center"}}
                    open={alert.isVisible && alert.type === AlertType.success}
                    autoHideDuration={null}
                >
                    <Alert severity="success" variant={"filled"}>
                        <IconButton onClick={() => setAlert(emptyAlert)} className={"alertCloseIcon"}>
                            <CloseIcon/>
                        </IconButton>
                        <AlertTitle>Erfolgreich</AlertTitle>
                        {alert.message}
                    </Alert>
                </Snackbar>
                <Grid container direction={"row"} className={"gridContainer"}>
                    <Grid item xs={10}>
                        <Search setTranslations={setTranslations} sortTranslations={sortTranslations}
                                showErrorAlert={showErrorAlert} showSuccessAlert={showSuccessAlert}
                                resetTable={resetTable}/>
                    </Grid>
                    <Grid item xs={2}>
                        <Button variant="contained" color="primary" id={"addButton"} onClick={addTranslation}>
                            Neue Ãœbersetzung
                        </Button>
                    </Grid>
                </Grid>
                <TranslationTable translations={translations} updateTranslation={updateTranslation}
                                  deleteTranslation={deleteTranslation}
                                  handleSort={handleSort} setTranslations={setTranslations}
                                  order={order} orderBy={orderBy}
                />
            </MuiThemeProvider>
        </div>

    );
}

export default TranslationWrapper;
