import React, {useEffect, useState} from 'react';
import '@axa-ch/button'
import '@axa-ch/table';

export default function TranslationTable() {

    const BASE_URL = "http://localhost:8080/translation"

    const [translations, setTranslations] = useState([])

    useEffect(() => {
        loadAllTranslations()
    }, [])

    function loadAllTranslations() {
        const requestOptions = {
            method: 'GET',
            headers: ({
                'Content-Type': 'application/json'
            })
        };

        fetch(BASE_URL, requestOptions)
            .then(response => response.json())
            .then(
                data => setTranslations(data)
            )
    }

    function split_at_index(value, index) {
        return value.substring(0, index) + " " + value.substring(index);
    }

    return (
        <axa-table innerscroll="500" maxheight="450">
            <table>
                <thead>
                <tr>
                    <th>App-ID</th>
                    <th>Text-ID</th>
                    <th>Kontext</th>
                    <th>Stage</th>
                    <th>DE</th>
                    <th>EN</th>
                    <th>FR</th>
                    <th>IT</th>
                    <th>zuletzt ge√§ndert</th>
                    <td></td>
                    <td></td>
                </tr>
                </thead>
                <tbody>
                {translations.map(tr =>
                    <tr>
                        <td>{tr.textId.length > 14 ? split_at_index(tr.textId, (tr.textId.length / 2)) : tr.textId}</td>
                        <td>{tr.appId}</td>
                        <td>{tr.content}</td>
                        <td>{tr.stage}</td>
                        <td>{tr.german}</td>
                        <td>{tr.english}</td>
                        <td>{tr.french}</td>
                        <td>{tr.italian}</td>
                        <td>{tr.lastModified}</td>
                    </tr>

                )}
                </tbody>
            </table>
        </axa-table>
    )
}
